import React, { useState } from 'react';
import { TextField, Button, Container, Typography } from '@mui/material';

const Form = () => {
    const [formData, setFormData] = useState({ name: '', email: '', message: '' });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch('http://localhost:5000/submit-form', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData),
        })
            .then((response) => response.text())
            .then((data) => {
                console.log(data);
                // Handle successful form submission here
            })
            .catch((error) => {
                console.error('Error:', error);
                // Handle error here
            });
    };

     return (
        <div>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Client Code (type in an S2K id)</label>
                    <input 
                        type="text" 
                        id="client-code" 
                        name="client-code" 
                        value={formData.clientCode} 
                        onChange={handleChange} 
                        required 
                    />
                </div>
                <div>
                    <label>Merchant Code (eg. SSKIIS)</label>
                    <input 
                        type="text" 
                        id="merch-code" 
                        name="merch-code" 
                        value={formData.merchantCode} 
                        onChange={handleChange} 
                        required 
                    />
                </div>
                <div>
                    <label>Transaction Currency</label>
                    <input 
                        id="text" 
                        name="txn-currency" 
                        value={formData.txnCurrency} 
                        onChange={handleChange} 
                        required 
                    />
                </div>
                <div>
                    <label>Transaction Amount</label>
                    <input 
                        id="text" 
                        name="txn-amount" 
                        value={formData.txnAmount} 
                        onChange={handleChange} 
                        required 
                    />
                </div>
                <div>
                    <label>Merchant Service Charges</label>
                    <input 
                        id="text" 
                        name="merch-service-charges" 
                        value={formData.merchantServiceCharges} 
                        onChange={handleChange} 
                        required 
                    />
                </div>
                <div>
                    <label>Merchant's Reference Number</label>
                    <input 
                        id="text" 
                        name="merch-ref-number" 
                        value={formData.merchantReferenceNumber} 
                        onChange={handleChange} 
                        required 
                    />
                </div>
                <div>
                    <label>Success Static Flag (Y)</label>
                    <input 
                        id="text" 
                        name="success-flag" 
                        value={formData.successFlag} 
                        onChange={handleChange} 
                        required 
                    />
                </div>
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default Form;
